const scenes = document.querySelectorAll('.scene');const prevButton = document.getElementById('prevButton');const nextButton = document.getElementById('nextButton');const demoButton = document.getElementById('demoButton');let currentScene = 0;function showScene(index) {scenes.forEach((scene, i) => {if (i === index) {scene.classList.add('active');} else {scene.classList.remove('active');}});}showScene(currentScene);nextButton.addEventListener('click', () => {currentScene = (currentScene + 1) % scenes.length;showScene(currentScene);});prevButton.addEventListener('click', () => {currentScene = (currentScene - 1 + scenes.length) % scenes.length;showScene(currentScene);});demoButton.addEventListener('click', () => {alert('Demo requested!');});